async () => {
  const now = new Date();
  if (!appsmith.store.login?.created_at) {
    navigateTo("login");
    return;
  }
  const createdAt = new Date(appsmith.store.login?.created_at);
  const dif = Math.floor(now.getTime() - createdAt.getTime()) / 1000;
  if (dif > appsmith.store.login.expires_in) {
    navigateTo("login");
  }
}

async () => {
  if (appsmith.URL.queryParams.id) {
    await get_event_by_id.run();
    const eventChannels = get_event_by_id.data[0].event_stream_channels.map(el => {
      return {
        stream_name: el.stream_name,
        channel_id: el.channel.channel_id,
        logo: el.logo
      };
    });
    await storeValue("channels", eventChannels);
  } else {
    await storeValue("channels", [{
      stream_name: "",
      channel_id: "",
      logo: ""
    }]);
  }
}
